	function genRand() {
		$randomized = 'id="';
		
		for ($i = 0; $i < 10; $i++) {
			$randomized .= substr(str_shuffle("0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"), 0, 50);
		}
		return $randomized . '"';
	}
	
	function genJunk() {
		$randomized = '<!--';
		
		for ($i = 0; $i < 30; $i++) {
			for ($j = 0; $j < 5; $j++) {
				$randomized .= substr(str_shuffle("0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"), 0, 50);
			}
			$randomized .= "\r\n";
		}
		return $randomized . '!-->';
	}
	
	function isMobile() {
